// http://web.orionstars.org/hot_play/orionstars/index.html

<!DOCTYPE html>
<script>
	window.parent.Page = "Login";
	window.parent.Balance = -1.0;
	window.parent.Grand = -1.0;
	window.parent.Major = -1.0;
	window.parent.Minor = -1.0;
	window.parent.Mini = -1.0;
</script>
<html>
	<head>
		<meta charset="utf-8">
		<title>orionstars</title>
		<meta name="full-screen" content="yes"/>
		<meta name="x5-fullscreen" content="true"/>
		<meta name="360-fullscreen" content="true"/>
		<meta name="viewport" content="width=device-width,initial-scale=1,maximum-scale=1,user-scalable=no">
		<meta name="apple-mobile-web-app-capable" content="yes">

		<link rel="shortcut icon" href="appIcon_3.png" type="image/x-icon" />

		<!--禁止缓存标识-->
		<meta http-equiv="Expires" content="0">
		<meta http-equiv="Pragma" content="no-cache">
		<meta http-equiv="Cache-control" content="no-cache">
		<meta http-equiv="Cache" content="no-cache">

		<!--    1.制作4中不同尺寸的图片:-->
		<meta content="yes" name="apple-touch-fullscreen">
		<meta content="white" name="apple-mobile-web-app-status-bar-style">
		<!--适用于 Retina 屏的 iPad-->
		<link rel="apple-touch-icon-precomposed" sizes="144x144" href="appIcon_1.png"/>
		<!--适用于 Retina 屏的 iPhone-->
		<link rel="apple-touch-icon-precomposed" sizes="120x120" href="appIcon_2.png"/>
		<!--适用于非 Retina 屏的 iPad-->
		<link rel="apple-touch-icon-precomposed" sizes="72x72" href="appIcon_3.png"/>
		<!--适用于非 Retina 屏的 iPhone-->
		<link rel="apple-touch-icon-precomposed" href="appIcon_1.png"/>
	</head>
    <body>
		<script src="../commonFrame/current-device.min.js"></script>
		<script src="../vconsole.min.js"></script>
		<!-- <script src="../commonFrame/index.min2.js"></script> -->
		<script src="https://cdnjs.cloudflare.com/ajax/libs/crypto-js/4.0.0/crypto-js.min.js"></script>
		<script src="../commonFrame/index2.min.js"></script>
		<script src="../commonFrame/gameLogin.min.js"></script>
		<script src="../commonFrame/Net.min.js"></script>
		<script src="../commonFrame/BsLangText.min.js"></script>

		<!-- <label id = "jumpUrl" style="display:none;">https://uutool.cn/js/</label> -->

		<!--enterGame和splash元素显示互斥，用代码控制变化-->
		<div id="zoomResize">
			<div id = "enterGame" style="display:none;">
				<!-- <img class = "aligncenter" src="loading.png"> -->
				<label id = "tipText" class="aligncenter"></label>
				<div class="btnParent">
					<button id ="btnExit"></button>
				</div>
			</div>
			<div id="splash">
				<img src="splash.jpg"/>
			</div>
		</div>
		

		<iframe id="frmDialog" width="100%" height="100%" allowfullscreen="true" webkitallowfullscreen="true" mozallowfullscreen="true" oallowfullscreen="true" msallowfullscreen="true" scrolling="no" frameborder="0">
			<p>Your browser does not support iframes.</p>
		</iframe>

		<div id = "tip" class="box">	
			<div id="slideTip" class="aligncenter equalRatio">
				<div class="mask"></div>
				<div class="aligncenter">
					<h1 align = center style = "width: 390px; color: rgb(255, 255, 255);">Slide up to jump screen.</h1>
					<p align = center ><img src="../commonFrame/slideTip.png"/></p>
				</div>	
			</div>
			<div id="clickTip" class="aligncenter equalRatio">
				<!-- <p  align = center style="color: rgb(255, 255, 255); font-size: 5vw;">Please touch the screen.</p> -->
				<!-- <div align = center>
					<img src="../commonFrame/clickFullScreen.png" style="width:20vw"/>
				</div>				 -->
			</div>
		</div>

		<div id="disconnect" class="aligncenter hide">
			<img src="../commonFrame/disconnectTip.png" class="equalRatio">
			<input id="refresh" type="image" value="refresh" onclick="refreshClick()" src="../commonFrame/refresh.png"/>
		</div>
    </body>
	<style>
		* {
			margin:0;
			padding:0;
		  }
		body {
			overflow: hidden;  
			background-color: rgb(0, 0, 0);
		}
		.box{
			top:0;
			bottom:0;
			left:0;
			right: 0;
			width: 100%;
			/* height: 10000px; */
			/* background-color:rgb(82, 82, 82); */
			position: absolute;
		}
		.aligncenter {
			position: fixed;
			left: 50%;
			top: 50%;
			transform:translate(-50%, -50%);
		}
		.mask {
			height: 100%;
			background-color:rgb(82, 82, 82);
			opacity: 0.5;
		}
		.equalRatio{
			width:100%;
		}
		.hide{
			display: none;
		}
		
		#slideTip {
			width: 100%;
			height: 100%;
			display: none;
		}
		#frmDialog {
			position: absolute;
			display: none;
		}
		#splash {
			display: none;
			position: absolute;
			left:50%;
			top: 50%;
			transform:translate(-50%, -50%);
		}
		#splash img {
			width: 1667px;
			height: 750px;
		}
		#refresh {
			width: 18%;
			position: absolute;
			left:50%;
			top: 70%;
			transform:translate(-50%, -50%);
		}
		#tipText {
			color: red;
			font-size: 3vh;
		}
		.btnParent{
			width:1280px;
			height:720px;
			left:50%;	
			top:50%;	
			position: absolute;
			-webkit-transform: translate(-50%, -50%);
		}
		
		#btnExit{
			display:block;
			position: absolute;
			border-style :none;
			background-image: url('Exit.png'); /* 替换为你的图片路径 */ 
			width:121px;
			height:36px;
			left:59px;
			top:55px;
		}
		/* @supports (bottom: env(safe-area-inset-bottom)) {
			.menu_wkBox.back_box{
				padding-bottom: env(safe-area-inset-bottom);
			}
		} */
	  </style>
</html>
